INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_5', 'Es un placer volver a verte, amigo. ¿En qué puedo ayudarte?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_6', 'Eres siempre bienvenido aquí, amigo. ¿Qué tienes en mente?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_7', 'Hola amigo mío. ¿Ha venido a beneficiar a nuestras dos grandes naciones?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_8', 'Es muy bueno ver una cara amiga. ¿En qué puedo ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_9', 'Bienvenido, amigo. Por favor, quédese. Tendré mis siervos listos para preparar una fiesta maravillosa para celebrar su llegada.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_10', 'Hoy nos reunimos, mi amigo. ¿Cómo puede mi gloriosa nación ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_11', 'Tenía la sensación de que su amable rostro aparecería por aquí. ¿Qué puede hacer mi imperio para ayudar a un líder honorable como usted?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_12', 'Mis ciudadanos han quedado satisfechos con el contacto con su imperio. Esto sólo puede beneficiar a ambos. Ahora bien, ¿en qué puedo ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_13', 'Es genial ver a un amigo. ¿En qué podemos ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_14', 'Ha venido al lugar correcto para la diplomacia. ¿Qué quiere discutir?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_7', 'Hola, ¿qué negocios tiene que discutir conmigo hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_8', '¿En qué puedo ayudarle? ¿Y cómo puede ayudarme?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_9', 'Saludos. Espero que se presente ante mí con intenciones honestas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_10', 'Hola. ¿Puedo ofrecer algo de mi mejor vino mientras conversamos?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_11', '¿En qué puedo ayudarle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_12', 'Saludos. ¿De qué desea hablar hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_13', 'Hola. ¿En qué puedo ayudarle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_6', 'Tengo asuntos urgentes que atender. No me haga perder el tiempo aquí.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_7', 'Su compañía no es bienvenida aquí. Le ruego que haga su visita a mi tierra lo más corta posible.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_8', 'Estás perdiendo tu tiempo aquí. ¿Qué puedes ofrecerme?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_9', '¿No eché a tu emisario de mis tierras? Tienes muchas agallas para venir a visitarme.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_10', 'No me pongas a prueba. Estás poniendo a prueba mi paciencia. ¿Qué es lo que quieres ahora?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_11', 'Actualmente no estas en mi lista de prioridades. ¿Qué es lo que quieres para hacer perder mi tiempo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_12', 'Oh, excelente. Es usted. Dígame, ¿por qué me ha deshonrado con su insoportable hedor?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_13', 'No mereces mi tiempo. Ahora, vete. ¿por qué sigues aquí?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_14', 'Ya es bastante malo que tengan que lidiar con tu inmundicia los que viven en tu propio pais. ¿Y ahora por qué te has arrastrado hasta mi presencia?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_15', 'Quisiera mostrar mi respeto y ofrecerle vino, pero usted no es más que un indigno bárbaro que sólo merece ser alimentado con veneno. Ahora, manos a la obra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_4', 'Debemos poner fin a esta lucha. Nuestros pueblos desean poner fin a esta violencia sin sentido.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_5', 'Estoy dispuesto a negociar un alto el fuego, si lo quieres. Esta guerra ha durado demasiado tiempo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_6', 'Posponer el fin de esta guerra sólo te hace parecer más aún el villano. Por favor, pon fin a esta locura y volvamos a la paz.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_7', '¿No hay una manera más civilizada para poner fin a nuestras hostilidades?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_8', 'La lucha solo hace el mundo un lugar peor. No somos bárbaros. Tenemos que dejar de actuar como tales.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_9', 'Buen líder, ¿me ayudará a poner fin a esta tensión? Es sólo mi esperanza de construir la paz entre nuestros pueblos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_10', 'Les pedimos nada más que un retorno a la sensibilidad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_11', 'Es hora de dejar el pasado atrás y resolver nuestras diferencias. El derramamiento de sangre no es la respuesta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_12', 'Es hora de que nos demos cuenta de que lo único que hemos logrado aquí sea intercambiar dolor entre nuestros seres queridos perdidos. Debemos poner fin a esta guerra, por el bien de nuestro pueblo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_5', '¿De verdad creías que ibas a salir de ésta con tanta facilidad? Se ha ido!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_6', '¿Por qué estás aquí? Esto es una broma, ¿verdad?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_9', 'Es hora de enfrentar la realidad. Yo gano, tú pierdes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_10', 'No habrá paz. Todas tus ciudades nos pertenecen.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_11', 'Es hora de enfrentar la realidad. Yo gano, tú pierdes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_12', 'Su tierra está llena de delincuentes, y yo soy su juez. Ahora, fuera de mi vista, tengo un asunto que resolver.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_13', '¿Oyes eso? Ese es el sonido de su pecado clamando por la salvación. Lo bueno es que estoy aquí.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_2', 'Espero que sus científicos estén trabajando duro para que podamos cumplir nuestro acuerdo de investigación. Ruego por que valga la pena el dinero que gastamos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_3', 'Dios te salve, líder poderoso. ¿Tus científicos trabajan duro para completar nuestro acuerdo de investigación?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_4', 'Gastamos un montón de oro en ese acuerdo de investigación. Espero que hagamos descubrimiento a no mucho tardar', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_2', 'Es bueno verte de nuevo, amigo. Espero que su imperio esté en condición estable. ¿En qué puedo ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_3', '¡Salve, amigo! ¿Cómo puedo ayudar a su gran nación el día de hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_4', 'Es bueno verte de nuevo, amigo. ¿Necesitas algo de mí?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_5', 'Saludos, amigo. Tenemos negocios que llevar a cabo, ¿no?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_4', 'Estoy harto de {1_ThirdPlayerName: textkey}. Espero que algún día sea expulsado de nuestras vidas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_5', 'No entiendo cómo villanos como {1_ThirdPlayerName:textkey}  pueden llamar a su gente civilizada. Tiene que ser estúpido.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_6', '¿No está de acuerdo conmigo en que {1_ThirdPlayerName: textkey} es un problema para el mundo entero? Tenemos que informar a los demás acerca de sus pecados.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_2', '¿No ha sido destruido todavía? Estoy sorprendido. Yo creía que los bárbaros eran más fáciles de vencer que eso. Seguiremos esperando pues.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_3', '¿No ha sido aplastado por el poder de sus enemigos todavía? Tal vez debería darles el empujón necesario. ¿No sería eso bueno?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_4', 'Mis asesores estaban discutiendo sobre su pequeña guerra. Tengo derecho a decir que creo que es bastante patética.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_5', 'Su pequeña guerra está dando mucho trabajo a mis bufones. Quiero ser claro, su intento de supervivencia es una especie de broma.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HUMAN_AT_WAR_2', 'Siento mucho que esté tan ocupado en la guerra. ¿Puedo ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HUMAN_AT_WAR_3', 'Enviad mis saludos a sus hombres en combate. Ahora bien, ¿en qué puedo servirle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_MILITARY_2', 'No creais que no me he dado cuenta de sus tropas apostadas en mis fronteras. Si quereis pelea, entonces venid. No os escondais como un cobarde.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_MILITARY_3', 'No me siento amenazado por sus tropas. Atáqueme como el "gran" líder es. O salga de mi vista.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_MILITARY_2', 'Mis generales han notado un gran número de tropas cerca de mis fronteras. Respetuosamente le insto a evitar mis tierras, permitiendo a mis ciudadanos vivir felices. Ahora bien, ¿tienes algo que preguntarme?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_MILITARY_3', 'Me gustaría permanecer en buenos términos con usted, pero el posicionamiento de su ejército está haciendo que sea extremadamente difícil. Por favor, reconsiderare la ubicación de sus tropas o puede que tengamos que abandonar la diplomacia.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_EXPANSION_2', 'La expansión de su gente es una molestia para mí y todo el mundo que nos rodea. Pero no debería tener que preocuparse, sus edificios probablemente erosionarán desde esa porquería a la que llama nación.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_EXPANSION_3', 'Me recuerda a un enjambre de langostas. Se extiende por el mundo y seguirá propagándose hasta que haya dejado el mundo entero devastado. Sólo puedo esperar que se de cuenta del desagradable dolor de cabeza en el que se ha convertido en su expansión.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_EXPANSION_2', 'Al parecer, su expansión ha llamado la atención de uno de mis asesores extranjeros. Sospecha que puede quedarse sin espacio y puede abrir fuego contra mi pueblo. Les insto a frenar su expansión. ¿Qué dice usted?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_PLOT_BUYING_2', 'Tirar su dinero en las tierras a su alrededor sólo me da más territorio que capturar. Espero haber sido bastante claro.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_PLOT_BUYING_2', 'Se aceptar una compra justa de vez en cuando, pero la velocidad a la que está comprando la tierra que nos rodea es alarmante. Le insto a que reconsidere esta tensión entre nuestras naciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_MILITARY_2', 'Su ejército es muy poderoso. Sólo puedo esperar que utilizará su fuerza para el bien y no para la barbarie.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_MILITARY_3', 'He oído leyendas de grandes héroes y batallas de su imperio. Sus enemigos deben temer el día en que sus caminos se crucen.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_ECONOMY_2', 'Mis comerciantes han quedado satisfechos con las visitas a su gran imperio. Tal vez le gustaría enseñarles algunas cosas acerca de hacer dinero.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_ECONOMY_3', 'Sus arcas están a rebosar, mientras que la mías permanecen vacías. Si pudiera enseñar a mis recaudadores de impuestos a mantener una economía fuerte, le estaría muy agradecido.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_BY_AI_3', 'Me hablaron mal de vos por alguna razón. ¿Por qué me molestais con tonterías? Idos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_AI_3', 'Ya no parece tan enfadado, ¿verdad? No me lo creo. No es más que un falso. Fuera de mi vista.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TOO_SOON_FOR_DOF_3', 'Una oferta interesante. Aunque aprecio la cortesía, no se aún lo suficiente como para confiar en vos. ¿Tal vez en otro momento?.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TOO_SOON_FOR_DOF_4', 'Pareceis un líder amable, pero no me puedo permitir Alianzas todavía. ¿Tal vez en el futuro?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DOF_NOT_HONORED_3', '¡Me bañaba en la mentira y me apuñaló por la espalda! No es ústed digno de la atención de nadie. Fuera de mi vista [NEWLINE] [NEWLINE]! ([COLOR_WARNING_TEXT] Nos han denunciado públicamente NOTA: no estáis en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DOF_NOT_HONORED_4', 'Me recuerda a un niño codicioso. Quiere demasiado a demasiada gente y al final no se queda con nadie. No es más que un mentiroso y ahora todo el mundo va a saber de sus tácticas traicioneras. [NEWLINE] [NEWLINE]. ([COLOR_WARNING_TEXT] Nos han denunciado públicamente NOTA: no estái en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DENOUNCING_FRIEND_3', 'Ha mostrado sus verdaderos colores. La única acción que puedo tomar ahora es advertir a otros de su inutilidad. [NEWLINE] [NEWLINE] ([COLOR_WARNING_TEXT] Nos han denunciado públicamente NOTA: no estáis en guerra [ENDCOLOR]).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DENOUNCING_FRIEND_4', 'Nadie merece malgastar su tiempo en vos. Trabajar juntos fue el mayor error de mi vida. No estoy interesado en repetirlo. [NEWLINE] [NEWLINE]. ([COLOR_WARNING_TEXT] Nos han denunciado públicamente NOTA: no estáis en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_2', 'Usted y su pequeño amigo {1_CivName} me enferman. No puedo esperar a ver el día que sus imperios ardan en llamas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_2', 'No voy a estar de pie y jugar como un tonto, gusano. Si buscas pelea, luchemos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_3', 'Te reto a que nos ataques. Sus ciudades serán un grandioso decorado para mis banderas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_4', '¿Deseais derramar sangre? Venid por mí. No puedo esperar.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_EXPANSION_SERIOUS_WARNING_2', '¿No te advertí acerca de su expansión imprudente? Este asunto es serio. ¡Aléjaos de mi tierra o la acción militar será inevitable. ¿Entendeis?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DENOUNCE_4', 'No es más un demonio que no merece reconocimiento. [NEWLINE] [NEWLINE] ([COLOR_WARNING_TEXT] Nos han denunciado públicamente NOTA: no estáis en guerra [ENDCOLOR].).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DENOUNCE_5', 'No sois más que un tonto malgasto de tiempo. Olcultaos en vuestro rincón del mundo, yo no tengo más tiempo para vos. [NEWLINE] [NEWLINE]. ([COLOR_WARNING_TEXT] Nos han denunciado públicamente NOTA: no estáis en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DENOUNCE_6', 'Ahora el mundo sabe que un idiota lleva su patética nación. Vais a conducir a su gente a una extinción temprana. [NEWLINE] [NEWLINE]. ([COLOR_WARNING_TEXT] Nos han denunciado públicamente NOTA: no estáis en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DOW_LAND_4', 'La cercanía de nuestras fronteras ha provocado mucha tensión. Al fín es el momento de ver de parte de quién está la victoria en el combate.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DOW_LAND_5', 'Es hora de expandir mi imperio. Os declaro la guerra sin el más mínimo remordimiento.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_5', 'Todavía no puedo creer que pensaráis que yo era vuestro aliado. Esa ignorancia será su perdición.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_6', 'La amistad fue bonita mientras duró, ¿no? Siento decírtelo, pero no erais más que un peón en mi gran estrategia. Es hora de morir.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_7', 'Tiempo de morir, cachorro. Estaba cansado de jugar el papel de su amigo. Es hora de hacer valer mi dominio.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DOW_WEAK_BETRAYAL_2', 'Sé que apenas tengo una pequeña posibilidad de victoria, pero si no atacamos ahora, sólo nos enfrentaremos a un oponente más fuerte en el futuro.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HUMAN_PLOT_BUYING_WARNING_GOOD_2', 'Me alegra saber que entiende nuestra situación. Espero que podamos continuar con nuestra comprensión mutua.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HUMAN_EXPANSION_WARNING_GOOD_2', 'Gracias por su comprensión. No lo olvidaremos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_5', 'Estaba tan seguro de que lo entendería... Ha decepcionado a mi pueblo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_6', 'Aléjate de mi vista. Eres una desgracia para mi pueblo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_GOOD_2', 'Muy bien. Sin embargo, no le quitaré el ojo de encima a sus ejércitos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_GOOD_3', 'Que así sea.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_2', 'Patético cachorro. Aunque supongo que no puedo quejarme de vos. Está en su naturaleza bárbara el tomar decisiones imprudentes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_3', 'Honesto o no, sus decisiones insensatas me han hecho perder cualquier respeto por vos. Nuestros ejércitos solventarán nuestras diferencias a partir de ahora.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_4', 'Puede que no seais un cobarde, pero eso no quiere decir que no seais un tonto. Sois una vergüenza para todos los líderes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_AGGRESSIVE_MILITARY_WARNING_BAD_2', 'Ha traicionado nuestra confianza. Estoy decepcionado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_AGGRESSIVE_MILITARY_WARNING_BAD_3', 'Si esta es la manera que debe ser, que así sea. Os destruiré a ti ya tus malvados ejércitos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_4', 'Ha demostrado compasión donde otros han demostrado codicia. Por ello, le aplaudo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_5', 'Estoy agradecido por la liberación de nuestros ciudadanos. Ha demostrado ser un aliado más fiable que lo que pensaba. Espero que continuen estas buenas relaciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_6', 'Tengo la suerte de recibir a mi pueblo otra vez. Tiene mi agradecimiento.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_7', '¡Habeis devuelto a estos prisioneros de guerra! Este gesto no será olvidado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_8', 'Gracias por la liberación de mi pueblo. Sois un líder bondadoso y compasivo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_4', '¡Debemos abrir una botella de champán para celebrar su bondad!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_5', 'Estoy muy agradecido por este acto desinteresado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_6', 'Nuestra gente sólo puede beneficiarse de esto. Gracias.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_4', '¿Qué trato terminará con nuestro sufrimiento mutuo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_5', 'Estoy dispuesto a escuchar las ofertas de paz. ¿Cuál es su oferta?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_6', 'En nombre de mi pueblo, le insto a llegar a un acuerdo apresurado. ¿Cuál es el precio de la paz?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DOW_DESPERATE_9', 'Mis asesores pueden decirme lo contrario, pero yo soy el que manda. Tengo pocas esperanzas de ganar, ¡pero tengo que vencer!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_4', ' Éste es mi momento. Me levantaré para convertirme en rey de reyes. ¡Naciones más pequeñas como la vuestra se doblegará ante mi poder!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_7', ' No puedo pasar por alto esta oportunidad para fortalecer mi pueblo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_3', 'Tengo curiosidad por escuchar su oferta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_4', 'Por favor, haga su oferta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_5', 'Vamos a ver su propuesta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_5', '¿Intentáis poner límites a mis fronteras? No. Tengo un derecho a establecerse donde me plazca.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_6', '¿Por qué no? Si no os gusta, haced algo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_7', '¡Por supuesto que no, maldito grosero! Recordaré esta afrenta. No quiero llevar a cabo negociaciones con un bruto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_', '/ * AI Ofertas Comerciales * /', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_RESEARCH_AGREEMENT_OFFER_2', '¡El futuro es brillante por delante y mis científicos están dispuestos a explorar lo desconocido! ¿Nos ayudais con un acuerdo de investigación mutua?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_RESEARCH_AGREEMENT_OFFER_3', 'Un grupo de científicos es bueno. ¿Pero imagínese lo que ambos podríamos lograr? ¿Va a ayudarnos con un acuerdo de investigación?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_LUXURY_TRADE_2', 'Un intercambio de estos recursos Lujo haría a nuestro pueblo feliz. ¿Qué dice usted?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_REQUEST_ITEM_4', 'Malos tiempos han caído sobre mi pueblo. Solicito su ayuda para ayudarnos. ¿Puede ofrecerselo un amigo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_REQUEST_ITEM_5', 'Los amigos debemos ayudarnos el uno al otro. He caído en tiempos difíciles. ¿puedes ayudarnos? No lo olvidaré.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_PEACE_OFFER_4', 'Creo que ha llegado el momento de deponer las armas y negociar la paz. ¿Va a firmar un tratado de paz con nosotros?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_PEACE_OFFER_5', 'No somos bárbaros sedientos de sangre. Debemos poner fin a esta guerra mientras aún tenemos nuestra dignidad. ¿Va a aceptar este trato?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_PEACE_OFFER_6', 'Un día un niño se me acercó y me dijo que su padre fue asesinado por sus hombres. Su vida estaba arruinada. Estas personas son nuestro futuro. No debemos traicionarles. Por favor, acepta esta ofrenda de paz.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_WORK_WITH_US_YES_4', 'Excelente. Espero una Alianza que cambiará el mundo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_WORK_WITH_US_YES_5', 'Acepto amablemente, en los términos que beneficien a ambas naciones y lucharé contra el mal que se encuentra fuera de nuestras fronteras.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_INSULT_GENERIC_3', 'No sé cómo su imperio se las arregla para mantenerse a flote. Uno pensaría que su gente se uniría a un imperio digno para vivir. ¡Ahora que lo pienso, mi nación sería un gran destino!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_WARMONGER_5', 'La gente está empezando a hablar de vos. Parecen pensar que estáis a favor de los genocidios de civiles inocentes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_WARMONGER_6', 'Sembrais el miedo por donde caminais. Las demás naciones están empezando a preocuparse. No puedo decir lo mismo de mí, yo sólo creo que eres un idiota balbuceante.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_BOOT_KISSING_3', 'Oh, gran líder. Te ruego que me enseñeis la forma de dirigir un imperio tan grande como el vuestro.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_3', 'Por favor, te lo ruego. Mostrad misericordia a mi pueblo, que no desea morir.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_4', 'Has reducido mi pueblo a cenizas. Su voluntad está hundida, sus hogares destruidos, las familias se han dispersado. Sólo puedo esperar que hayáis venido a poner fin a esta locura antes de que más inocentes mueran.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLOMACY_FRIENDSHIP_ADV_QUEST', 'Alianza', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLOMACY_FRIENDSHIP_HEADING3_BODY', 'Una alianza entre dos o más jugadores significa, en el sentido más básico que estos jugadores tienen un interés mutuo (por lo general se trata de convertirse en socios comerciales y el intercambio de recursos). Los jugadores también pueden optar por establecer bloques de aliados con varios jugadores con el fin de garantizar la seguridad a largo plazo y la estabilidad económica.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLOMACY_FRIENDSHIP_HEADING3_TITLE', 'Alianza', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_NTFN_DOF_S', '{1_PlayerName} aliado con {2_PlayerName}!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_NTFN_DOF', '{1_CivName: textkey} y {2_CivName: textkey} formado una alianza, forjando un fuerte vínculo entre los dos imperios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_NOTIFICATION_DOF_S', '{1_PlayerName} aliado con {2_PlayerName}!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_NOTIFICATION_DOF', '{1_CivName: textkey} y {2_CivName: textkey} formado una alianza, forjando un fuerte vínculo entre los dos imperios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_NOTIFICATION_FRIENDSHIP_EXPIRED_S', 'Alianza caducada', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_NOTIFICATION_FRIENDSHIP_EXPIRED', 'Su alianza con {1_LeaderName} siguió su curso y tendrá que ser renovada.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_DOF', '[COLOR_POSITIVE_TEXT] ¡Somos aliados! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_MUTUAL_DOF', '[COLOR_POSITIVE_TEXT] ¡Estamos aliados con los mismos líderes! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_HUMAN_DOF_WITH_ENEMY', '[COLOR_NEGATIVE_TEXT] ¡Se alió con uno de sus enemigos! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_REFUSED_REQUESTS', '[COLOR_NEGATIVE_TEXT] ¡Se negó la solicitud formulada por este jugador después de formar una Alianza! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_HUMAN_FRIEND_DECLARED_WAR', '[COLOR_NEGATIVE_TEXT] ¡Les hemos declarado la guerra mientras éramos aliados! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_HUMAN_FRIEND_DENOUNCED', '[COLOR_NEGATIVE_TEXT] ¡Les hemos denunciado mientras estábamos aliados! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_HUMAN_DECLARED_WAR_ON_FRIENDS', '[COLOR_NEGATIVE_TEXT] ¡Le has declarado la guerra a tus aliados! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_HUMAN_DENOUNCED_FRIENDS', '[COLOR_NEGATIVE_TEXT] ¡Has denunciado a tus aliados! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_DISCUSS_MESSAGE_DEC_FRIENDSHIP', '¿formamos una alianza?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DEC_FRIENDSHIP_1', 'Es difícil encontrar amigos dignos en este mundo. Me gustaría formar una alianza, informando a los otros imperios de la unión entre nuestros pueblos. ¿Qué me dices?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DEC_FRIENDSHIP_2', 'Gran líder, ¿le importaría aliarse con nosotros? Mi esperanza es que podamos construir una relación duradera entre los dos imperios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DEC_FRIENDSHIP_3', 'Vamos a plantar las semillas de un futuro brillante con la formación de una Alianza. ¿Qué decís?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DEC_FRIENDSHIP_4', 'Este mundo puede ser cruel. Sólo con la ayuda de otras personas podemos superarlo. ¡Vamos a anunciar al mundo que somos aliados!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DEC_FRIENDSHIP_5', 'Hola, gran líder. ¿Se aliará con nosotros para fortalecer el vínculo de nuestros imperios?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DEC_FRIENDSHIP_6', 'Sólo mediante la formación de una alianza pueden nuestros imperios realmente ser una fuerza temible en nuestro mundo. ¿me hará este honor?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DEC_FRIENDSHIP_7', 'Estoy buscando a un amigo en este mundo. ¿Se aliaría con nosotros?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DEC_FRIENDSHIP_8', 'Gran líder, ¿Se aliaría con nosotros? Esto haría que nuestros amigos cantaran de alegría y nuestros enemigos se acobardaran ante nuestras fuerzas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_', '/ * City Estados * /', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_3', 'Parece que hemos lanzado un ataque contra una de sus ciudades-estado. No debería contarse como una pérdida, ya que probablemente estaban planeando apuñalarnos por la espalda de todos modos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_4', 'En este momento estamos celebrando con una gran fiesta. Lanzar un ataque en contra de esa pequeña ciudad-estado fue la mejor decisión que hemos hecho en años.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_5', 'Oh, ¿oyes algo? Creo que puede ser el llanto de su protectorado. No te preocupes, mis hombres les silenciarán pronto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_3', 'Mi más sentido pésame, pero parece que en uno de sus protectorados con los que ha tenido relaciones en el pasado ha sido atacado por mis ejércitos. Tenga la seguridad de que esta decisión no se hizo en su contra, pero se consideró necesario un curso de acción algo más agresiva.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_4', 'Perdóname, pero mis ejércitos pueden haber sobrepasado sus límites y atacaron una ciudad-estado con la que tenía tratos en el pasado. Por favor concedeznos su perdón', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_5', 'Aunque mi intención no era agriar las relaciones con su pueblo, mis ejércitos han atacado una ciudad-estado con la que trataban en el pasado. Me gustaría repetir que esto no era una acción hostil hacia vos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_', '/ * Diferencias Diplomacia * /', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_DISCUSS_SETTLE_WHAT_WE_PLEASE', 'Nos expandiremos allá donde nos plazca.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_DISCUSS_SORRY_FOR_SETTLING', 'Lo sentimos, nos abstendremos de fundarlas cerca de su pueblo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_EXPANSION_WARNING_5', 'Me he dado cuenta de sus fronteras se han ido expandiendo a un ritmo alarmante. Asentando ciudades cerca de mi territorio podría arruinar cualquier posibilidad de amistad entre nuestros pueblos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_EXPANSION_WARNING_6', 'Historias antiguas de mi pueblo hablan de una migración desde las tierras que actualmente ocupan. Le permitiremos mantener las tierras por ahora, pero no dudaremos en tomar medidas si se expande más allá en las tierras de nuestros antepasados.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_VICTORY_DISPUTE', '[COLOR_NEGATIVE_TEXT] Si piensan que nos interesa un conflicto de intereses se equivocan', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_MINOR_THIS_GUY_WANTS_WAR', 'El líder que está hablando no tiene ningún deseo de ir a la paz con esta nación.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DIPLO_MINOR_OTHER_GUY_WANTS_WAR', 'El otro líder no tiene deseos de ir a la paz con el líder que está hablando.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_', '/ * Rankings Pop-Up * /', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_PROGRESS_SCREEN_GOLD', 'Las naciones más ricas del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_PROGRESS_SCREEN_HAPPINESS', 'Los países más felices del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_PROGRESS_SCREEN_POWER', 'Los países militarmente más fuertes del Mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_PROGRESS_SCREEN_PRODUCTION', 'Las naciones más productivas del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_PROGRESS_SCREEN_SCIENCE', 'Las naciones más avanzadas del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_PROGRESS_SCREEN_WONDERS', 'Las naciones más artísticas del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DUDE_8', 'Platón', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DUDE_9', 'Aristóteles', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DUDE_10', 'Magallanes', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DUDE_11', 'Charles Dezobry', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DUDE_12', 'Louis Halphen', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_DUDE_13', 'Barclay', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_', '/ * Se ha añadido 8/4/11 * /', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_5', '¿Es el exterminio total lo que realmente quieres? Le ruego que lo reconsidere.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_6', 'Esta guerra nos ha destruido. No te puedo ofrecer nada más que la paz ahora. Por favor, muestre misericordia.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_7', 'Mi pueblo no tiene ninguna posibilidad de sobrevivir. Nuestro destino está en sus manos ahora. Por favor, perdónenos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_8', 'Ha reducido mi glorioso imperio a nada más que un montón de escombros. Le ruego que nos permitan reconstruirla. Por el bien de nuestras relaciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ALEXANDER_FIRSTGREETING_4', 'Saludos Líder, ¡excelente! ¿Vos sois es el poderoso líder que Hermes dijo que aparecería en mi presencia?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ALEXANDER_FIRSTGREETING_5', 'Que los dioses estén con los dos, gran líder. Yo soy Alejandro III, todoporderoso rey de Grecia. Me siento honrado de conocerte.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ALEXANDER_FIRSTGREETING_6', 'Saludos, un extraño! Yo soy Alejandro, príncipe de los poderosos griegos. Rezo para que cenéis en la mesa de los olímpicos, y despreciéis a los Titanes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ASKIA_FIRSTGREETING_5', 'Por la gloria de Dios, te doy la bienvenida. Soy Askia, gobernante de la poderosa Songhai. Que nuestras dos naciones convivan en paz.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ASKIA_FIRSTGREETING_6', 'Saludos fabuloso líder. Espero que sea un hombre tan sabio como yo. Que la gracia de Dios mantenenga nuestras dos naciones libres de un conflicto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ASKIA_FIRSTGREETING_7', '¡Saludos! Soy Askia de la valiente Songhai. Deseo que seais un buen amigo y no un enemigo lamentable.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_AUGUSTUS_FIRSTGREETING_4', 'Por favor, acércate. Mi nombre es César Augusto, emperador del glorioso imperio de Roma. Deseo que sea un líder digno de confianza.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_AUGUSTUS_FIRSTGREETING_5', 'El pueblo romano le da la bienvenida. Yo soy Augusto, emperador de esta poderosa nación. ¡Algún día, mi nación crecerá para convertirse en el más grande imperio que el mundo haya visto!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_AUGUSTUS_FIRSTGREETING_6', 'Bienvenido a Roma, gran líder. Voy a servir a nuestro mejor vino para celebrar la reunión de nuestras dos naciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_BISMARCK_FIRSTGREETING_4', 'Bienvenido, gran líder. La poderosa nación de Alemania le da la bienvenida. Mi nombre es Bismarck. Deseo que su trato con el pueblo alemán sea con intereses positivos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_BISMARCK_FIRSTGREETING_5', 'Alemania le da la bienvenida a nuestra tierra, forastero. Yo, Otto Van Bismarck, espero que nuestras naciones se queden en buenos términos y beneficiarnos de esta reunión ..', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_BISMARCK_FIRSTGREETING_6', 'Yo te saludo, Extraño. Bienvenidos a Alemania. Mi nombre es Bismarck. ¿Te gusta el sabor de este vino exquisito?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_CATHERINE_FIRSTGREETING_4', 'Dios te salve, Extraño. Soy Catalina, emperatriz de Rusia. Espero que podamos llevarnos bien.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_CATHERINE_FIRSTGREETING_5', 'Salve, gran líder. Bienvenido a la tierra gloriosa de Rusia. Nuestra gente es valiente, y los hombres aquí son increíbles. Si vuestra nación se nos parece, entonces seremos la envidia de todo el mundo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_CATHERINE_FIRSTGREETING_6', 'Mis mayores cumplidos, gran líder. ¿Cómo puede el pueblo ruso hermosas servirle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_DARIUS_FIRSTGREETING_4', 'Dios te salve, líder. Yo soy Darío de Persia poderosa. Nuestro pueblo será la envidia de todo el mundo algún día.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_DARIUS_FIRSTGREETING_5', 'Saludos, líder. Mi nombre es Darío, de la poderosa Persia. Sólo puedo esperar que lleguéis a ser un aliado beneficioso.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_DARIUS_FIRSTGREETING_6', 'Bienvenido a Persia, Stranger. Yo soy Darío. Manténgase dentro de sus límites y de nuestra relación debe estar bien.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ELIZABETH_FIRSTGREETING_5', 'Inglaterra le saluda, líder. Yo soy la reina Isabel. ¿En qué podemos ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ELIZABETH_FIRSTGREETING_6', 'Bien hallado. Espero que seais de utilidad para Inglaterra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ELIZABETH_FIRSTGREETING_7', 'Excelente poder ver otra cara de este mundo. Soy Isabel, la reina de la poderosa Inglaterra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_GANDHI_FIRSTGREETING_4', '¡Saludos compañeros! Cantemos en reverencia a nuestro encuentro.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_GANDHI_FIRSTGREETING_5', 'Dios te salve, líder. ¿Has venido a difundir la palabra de la paz en el mundo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_GANDHI_FIRSTGREETING_6', 'Saludos. Yo soy Gandhi, de la pacífica India. Ruego por que nuestras dos naciones lleguen a un entendimiento.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_HARUN_FIRSTGREETING_4', 'Por la gracia de todo lo que es glorioso, Yo, Harun-al-Rashid, os doy la bienvenida a la tierra árabe.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_HARUN_FIRSTGREETING_5', 'Por favor, tome asiento líder. Soy Harun-al-Rashid, y la historia siempre recordará esta reunión.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_HARUN_FIRSTGREETING_6', 'Bienvenido a Arabia, gran líder. Mi nombre es Harun al-Rashid. ¿En qué puedo ayudarle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_HIAWATHA_FIRSTGREETING_4', 'Los espíritus han anunciado su llegada, gran líder.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_HIAWATHA_FIRSTGREETING_5', 'Saludos. Soy Hiawatha, líder de los poderoso iroqueses. Espero que compartan nuestro amor por la naturaleza.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_HIAWATHA_FIRSTGREETING_6', 'Dios te salve, Extraño. Los espíritus han dicho muchas cosas sobre tu pueblo, y me alegro de conocernos finalmente.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_5', '¡Ah! ¿Quién es usted y por qué usted se atreve a presentarse ante el poderoso Montezuma?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_6', '¡Soy Montezuma, líder de los especiales aztecas! ¿Y vos? Vos sois tan sólo uno de mis muchos temas pendientes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_7', '¡Cuidado, Extraño! Siempre hay sacrificios que deben hacerse en el imperio de Moctezuma.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_8', '¿Desea aliarse con el poderoso Moctezuma, forastero? Manden sus débiles y fuertes, sus mujeres y sus hijos. ¡Vamos a derramar sangre en nombre de nuestros dioses!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_NAPOLEON_FIRSTGREETING_5', 'Francia le da la bienvenida, Extraño. Yo soy Napoleón, temido general en todo el mundo! Espero que acabéis siendo aliado de Francia.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_NAPOLEON_FIRSTGREETING_6', 'Saludos, forastero. Yo soy Napoleón de Francia. Los franceses no fallan a nadie, tenga esto en cuenta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_NAPOLEON_FIRSTGREETING_7', 'Saludos. Yo soy Napoleón, y mi pueblo es audaz y valiente. Sólo se puede esperar que el suyo también lo sea.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_NAPOLEON_TRIBUTE_NO_ANGRY', '¿Cómo os atrevéis a exigirme un tributo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_NAPOLEON_DECLAREWAR_4', 'Oh, estoy seguro que habéis oído mis ejércitos franceses corriendo entre los árboles y desde lo alto de las colinas para destruirlos de una vez por todas!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ODA_FIRSTGREETING_4', 'Dios te salve, líder. Espero que sea tan honorable y valiente como yo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ODA_FIRSTGREETING_5', 'Bienvenido a Japón. Soy Oda Nobunaga. Rezo para que os convirtáis en un fiel compañero, o un adversario digno.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_ODA_FIRSTGREETING_6', 'Soy Oda Nobunaga, y les doy la bienvenida al gran imperio de Japón. Que vuestra estancia aquí sea tranquila y agradable.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_RAMESSES_FIRSTGREETING_4', 'Saludos, mortal. Yo soy Ramsés, el faraón del glorioso Egipto. ¿Habeis venido a inclinarse ante mi trascendencia?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_RAMESSES_FIRSTGREETING_5', 'Yo soy Ramsés, y ha entrado en la presencia de mi divinidad infinita.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_RAMKHAMHAENG_FIRSTGREETING_4', '¡Bienvenido a Siam, forastero! Estoy Ramkhamhaeng, rey de este pueblo glorioso!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_RAMKHAMHAENG_FIRSTGREETING_5', '¡Salve, gran líder! Bienvenido a Siam. Le invitamos a visitar mi jardín cuando le apetezca.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_RAMKHAMHAENG_FIRSTGREETING_6', 'Saludos! Bienvenido a Siam. Tenga en cuenta, los malvados no son bienvenidos en mi tierra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_SULEIMAN_FIRSTGREETING_4', '¡Yo te saludo, Extraño! Espero llegueis a ser un líder digno!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_SULEIMAN_FIRSTGREETING_5', '¡Salve, gran líder! Os saludo. Dadle la bienvenida a mi corte. Soy Suleiman, jefe de los otomanos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_SULEIMAN_FIRSTGREETING_6', '¡Bienvenido al Imperio Otomano, líder! Tengo el placer de conocerte.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_WASHINGTON_FIRSTGREETING_4', 'América le da la bienvenida, líder. Espero que sea ecúanime respecto a la libertad y la prosperidad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_WASHINGTON_FIRSTGREETING_5', 'Bienvenido a los Estados Unidos, extranjero. Soy Washington, gran gobernante del mundo libre. ¿Cómo podemos ayudar?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_WASHINGTON_FIRSTGREETING_6', 'Hola, jefe. Bienvenido a América, la tierra de los libres y el hogar de los valientes. Ahora, dime, ¿qué valor tenéis para nosotros?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_WUZETIAN_FIRSTGREETING_4', '¡Saludos! Soy Wu Zetian, de China. ¿Cómo puede mi pueblo ayudarle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_WUZETIAN_FIRSTGREETING_5', '¡Esta reunión será celebrada por nuestras generaciones futuras! Bienvenido a China, Extranjero.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_LEADER_WUZETIAN_FIRSTGREETING_6', '¡Saludos! ¿Cómo puedE, Wu Zetian de la poderosa China, ayudarles a usted y a su gente extranjera?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_LUXURY_TRADE_3', 'Nuestro pueblo exige un estilo de vida lujoso. ¿No estás de acuerdo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_LUXURY_TRADE_4', 'De acuerdo a este tratado, los habitantes de nuestras tierras serán mucho más feliz.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_LUXURY_TRADE_5', 'Es hora de que comercialicemos nuestros lujos para proporcionar una vida mejor a nuestros pueblos. ¿No estáis de acuerdo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_3', 'Primero tuve que lidiar con {1_CivName}, ¿ahora tengo que tratar con vos? Patético. Los dos sois la razón por la que este mundo es un condenado hervidero de escoria y villanía.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_4', 'Qué bonito. {1_CivName} son aliados. Está bien, porque voy a aplastar a ambos bajo la fuerza de mi imperio.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_EXPANSION_3', 'Lo siento, pero el ritmo de expansión de su gente me preocupa. Comprendo sus deseos de ampliar su influencia, pero nuestro pueblo está alarmado y se sienten amenazados. Sólo le pido si pudiera reducir la velocidad de expansión y dejar un poco de espacio para todos, entonces podremos llevarnos mejor. ¿Estáis de acuerdo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_PLOT_BUYING_3', 'Prosiga gastando su dinero en la compra de tierras sin valor. Es probable que ni siquiera pueda mantenerla a salvo de los animales salvajes, no menos de los ejércitos de los que le odian.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_PLOT_BUYING_3', 'Parece que has estado comprando tierras en torno a nuestro territorio demasiado rápido. Les insto a que cese en sus acciones, por el bien de nuestros pueblos y aliviar así nuestras tensiones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_14', '¿Por qué habéis venido? ¿Para rogar por la paz? ¡No! Mis ejércitos hudirán sus ciudades en la tierra, las mujeres y los niños caerán primero.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_15', 'Nunca me detendré hasta que malvados sean destruidos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_16', '¡Fuera! Esta guerra continuará hasta que esté listo para darle la misericordia!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_15', 'Salve, amigo! ¿Qué es lo que necesita de mi imperio?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_16', 'Encantado de verle de nuevo. ¿Ha venido a solicitar comercio?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_17', 'Es genial ver a un amigo, otra vez. Lo que importa es: ¿Qué vamos a discutir hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_6', 'Estoy escuchando.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_7', '¿Y bien? Vamos a escuchar su propuesta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_3', 'Lo siento, yo no creo que este acuerdo puede funcionar.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_4', 'No parece ser una forma lógica de llevar a cabo este trato.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_5', 'Lo siento, no veo un trato justo en ningún apartado de esta oferta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_6', 'No, lo siento. Este acuerdo no puede funcionar.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_4', '¿Eso es lo mejor que puede ofrecer? ¿Por quién me tomáis, por un tonto?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_5', 'Vamos, tendréis que hacerlo mejor si deseáis conseguir un acuerdo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_6', '¿Intentáis avergonzar a mi pueblo con semejante trato ultrajante?. Vais a tener que hacerlo mejor que eso.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_4', 'No puedo aceptar este acuerdo. Por favor, ponga algo más sobre la mesa.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_5', 'Este acuerdo no es bueno para mis intereses. Ofreced más y podemos hablar de un trato justo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_6', 'Creo que tiene que ofrecer un poco más en este acuerdo para poder trabajar. Pero tal y como está ahora, no es aceptable.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_HUMAN_CONCESSIONS_3', 'Este acuerdo será es el principio de un largo camino para hacernos amigos de nuevo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_HUMAN_CONCESSIONS_4', 'No nos olvidaremos este acuerdo, lo prometo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_4', 'Esta operación se llevará a cabo pues. Nuestro comercio continuará en el futuro.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_5', 'Los términos quedan establecidos. Esto es un trato justo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_6', 'Estoy de acuerdo con este acuerdo. Es justo para ambas partes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_4', 'No puedo rechazar esta generosa oferta de los suyos. Gracias.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_5', 'Han sacrificado sus bienes para hacer nuestra relación mejor. Por ello, le aplaudo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_6', 'Su generosidad no ha pasado desapercibida. Gracias.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_7', 'Recordaremos a este acuerdo en los próximos años. Gracias por ser tan generoso.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_3', 'Vamos a abrir nuestras fronteras para dar paso a un futuro comercio', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_4', 'Creo que es lo mejor para nosotros, como naciones civilizadas del mundo, abramos nuestras fronteras y dejemos que nuestra amistad prospere. ¿Qué decís?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_5', 'Vamos a abrir nuestras fronteras a nuestras naciones. Eso servirá para aumentar nuestra amistad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DEMAND_2', 'Seamos sinceros. Tenéis algo que yo quiero, y no lo queréis entregar. Del mismo modo podéis esperar para ver a mis tropas llamar a su puerta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_GENERIC_DEMAND_3', 'Dadme lo que quiero, y tus mujeres y los niños se mantendrán seguros. Eso suena a buen negocio, ¿no te parece?', '', '');
