INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_DISCUSS_MESSAGE_SORRY', 'We''re sorry this has caused a divide between us.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_DISCUSS_MESSAGE_DEAL', 'Get over it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_DISCUSS_MESSAGE_OKAY', 'Very well.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_DISCUSS_MESSAGE_YOULL_PAY', 'You''ll pay for this in time.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLOMACY_ANYTHING_ELSE', 'Anything else?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLOMACY_DISCUSS_WHAT', 'What would you like to discuss?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REPEAT_TOO_MUCH', '...', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REPEAT_TRADE_1', 'I like long walks, especially when taken by people who annoy me like you. Give a serious offer.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REPEAT_TRADE_2', 'I already told you to only bother offering a fair deal.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REPEAT_TRADE_3', 'Are you offering deals you know I won''t accept?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_REPEAT_1', 'History doesn''t repeat itself, but it does rhyme.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_REPEAT_2', 'I just got lost in thought. It was unfamiliar territory.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_REPEAT_1', 'Go away or I shall taunt you a second time.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_REPEAT_2', 'If it''s one thing I can''t stand, it''s people groveling.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_1', 'Here are my principles, and if you don''t like them... well, I have others.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_2', 'Wishing to be friends is quick work, but friendship is a slow ripening fruit.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_3', 'Where there is plenty of wine, sorrow and worry take wing.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_4', 'There cannot be a crisis next week. My schedule is already full.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_1', 'Everyone needs believe in something. I believe I''ll have more wine.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_3', 'What would you like to discuss?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_4', 'What brings you here today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_5', 'Go on.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_6', 'Let us begin.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_1', 'If ignorance is bliss, you must be ecstatic.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_2', 'I can be your best friend or your worst enemy. You seem to prefer the latter.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_3', 'Shock me, say something intelligent.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_4', 'Next time I see you, remind me not to talk to you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_5', 'I never forget a face, but in your case I''ll be glad to make an exception.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_1', 'I am not afraid of death, I just don''t want to be there when it happens.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_2', 'I am ready to meet my Maker. Whether my Maker is prepared for the ordeal of meeting me is another matter.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_1', 'An eye for an eye makes the whole world blind. Is it not time we ended this war?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_2', 'I would never die for my beliefs, because I might be wrong. Let''s discuss a ceasefire.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_3', 'I do not want the peace which passeth understanding, I want the understanding which bringeth peace.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_1', 'It''s just a flesh wound.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_2', 'You''re not dead yet?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_3', 'What are you going to do, bleed on me?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_4', 'The reports of my death were greatly exaggerated.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_1', 'Greetings. How goes progress towards the Research Agreement?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_BROKEN_MILITARY_PROMISE_1', 'What business does a dishonorable individual such as yourself have here?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_1', 'Always a pleasure working with you, friend. What brings you to my court?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_1', 'The villain {1_ThirdPlayerName:textkey} remains a constant headache. Don''t you agree?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_2', 'Greetings. My hope is that it is not long before we can finally deal with {1_ThirdPlayerName:textkey} once and for all.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_3', 'Any new plans for how to deal with {1_ThirdPlayerName:textkey}?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_COOP_WAR_1', 'It won''t be long before we''ll have dealt with {1_ThirdPlayerName:textkey}. I can just feel it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_COOP_WAR_2', 'I hope you''re looking forward to the downfall of {1_ThirdPlayerName:textkey} as much as I am.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_COOP_WAR_3', 'Soon we''ll have dealt with {1_ThirdPlayerName:textkey} once and for all.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_1', 'How''s your little war going? I won''t attend your funeral, but I will send a nice letter saying I approve of it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HUMAN_AT_WAR_1', 'Best of luck in your current conflict. What can I do for you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_MILITARY_1', 'I see your military forces near my borders. Are you here to foolishly threaten me or are you simply wasting my time?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_MILITARY_1', 'The positioning of your military is somewhat worrisome. I advise you to withdraw for the sake of future peace. Now, what would you like to talk about?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_EXPANSION_1', 'The spread of your cities and people is like a plague infecting the land. I look forward to the day I don''t have to see it any more.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_EXPANSION_1', 'While I have this opportunity, I should mention that your expansion has worried several of my officials. I hope that this does not cause any problems in the future. What would you like to discuss?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_PLOT_BUYING_1', 'Your theft of all the territory in your empire showcases your greed. You know that will catch up with you eventually, right?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_PLOT_BUYING_1', 'Your recent trend of buying land near us is concerning. For the sake of our future relationship I advise that you be cautious in this matter. Now that that''s out of the way, what brings you here today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_MILITARY_1', 'I''ve heard many stories of your powerful military. The villains of this world would be wise to fear your might.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_ECONOMY_1', 'Your empire is famed for its strong economy. Perhaps one day you might be willing to share some of your secrets with a friend?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_FEW_CITIES', 'The puny one makes an appearance. We were just talking about you. Only bad things, I assure you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_SMALL_ARMY', 'Just between you and me, your military is kind of the laughing stock of the world. Don''t let that get you down though, I''m sure someone will put you out of your misery soon.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_IS_WARMONGER', 'Ah, the bloodthirsty one shows his face. And what business does he have with me?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_BY_AI_1', 'You? What do you want? I have no more business with the likes of you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_BY_AI_2', 'And what do you think you''re doing here?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_AI_1', 'Oh, okay. So now you want to talk, huh?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_AI_2', 'Showing your face here after the things you''ve said? State your purpose quickly before I throw you out.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_OUR_DOF_WITH_ENEMY_OF_AI_1', 'What is it? I don''t have much time for those who hang around with the likes of {1_PlayerName}.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_OUR_DOF_WITH_FRIEND_OF_AI_1', 'Ah, hello. Always happy to talk with a friend of {1_PlayerName}.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_FRIEND_OF_AI_1', 'Oh, it''s you. I''ll be honest with you, I don''t take kindly towards those who insult {1_PlayerName}.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_ENEMY_OF_AI_1', 'Ah, greetings. Always happy to speak with other leaders who share my dislike of {1_PlayerName}.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TOO_SOON_FOR_DOF_1', 'I''m sorry, but we haven''t known one another for long enough for me to feel comfortable doing so. Perhaps we can revisit this subject in the future.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TOO_SOON_FOR_DOF_2', 'Give me more time to learn what kind of person you are. I don''t need a friend who changes when I change and who nods when I nod; my shadow does that much better.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DOF_AI_DENOUNCE_REQUEST_1', 'Friend, I have a serious matter to discuss. My relationship with {1_CivName} has broken down, and I need you to denounce them as a show of support. What do you say?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DOF_AI_WAR_REQUEST_1', 'I have found myself in a very serious situation, my friend. The time for war has come with {1_CivName}, and I need you to join me on the battlefield. What do you say? (WILL DECLARE WAR IMMEDIATELY)', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DOF_NOT_HONORED_1', 'Pathetic. So you now show your true colors. The world will know of your dishonor.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DOF_NOT_HONORED_2', 'So that is the kind of ally you are then? The rest of the world''s leaders will hear of your betrayal.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCING_FRIEND_1', 'I''ve had enough of you. I''ve told the other leaders of the world that working with you is a mistake.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCING_FRIEND_2', 'Alright, enough is enough. Informing everyone else of your ill deeds is long overdue.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_RESPONSE_TO_BEING_DENOUNCED_1', 'So is that how it is then? Very well. I shall not forget this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_RESPONSE_TO_BEING_DENOUNCED_2', 'You have sullied your reputation with such a foolish move.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_RESPONSE_TO_BEING_DENOUNCED_3', 'You have made a foolish mistake. This incident will not be forgotten.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_RESPONSE_TO_BEING_DENOUNCED_4', 'I had a feeling I was wasting my time with you. Little did I know it would come to this, however.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOFED_FRIEND_1', 'Ahhh, I''ve noticed that you''ve become Allies with {1_CivName}. Glad to hear of it. I''m already on good terms with them myself.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOFED_FRIEND_2', 'Any ally of {1_CivName} is a friend of mine.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOFED_FRIEND_3', 'Are you an ally with {1_CivName}, too? You have made a wise decision.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOFED_FRIEND_4', 'I see you''ve become Allies with my close friend, {1_CivName}. I foresee great things happening.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOFED_ENEMY_1', 'I recommend that you not get too close in your alliance with {1_CivName}. If so... we might have problems getting along.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOFED_ENEMY_2', 'Enjoy the state dinner celebrating your alliance with {1_CivName} - just count the silverware after they leave.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOFED_ENEMY_3', 'The friend of my enemy risks becoming my enemy. Keep that in mind during your alliance with {1_CivName}.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DENOUNCED_FRIEND_1', 'I''ll have you know that {1_CivName} and I have worked together in the past, and I have the utmost respect for them. Just so you''re aware.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DENOUNCED_FRIEND_2', 'How could you lie so brazenly about {1_CivName} - have you no decency?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DENOUNCED_FRIEND_3', 'When you spit in the face of {1_CivName}, you are spitting into the winds of war.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DENOUNCED_ENEMY_1', 'Glad to hear there are others in the world which share my disgust with {1_CivName}. They are clearly not to be trusted.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DENOUNCED_ENEMY_2', 'The day is coming when {1_CivName} is swept into the dustbin of history.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DENOUNCED_ENEMY_3', 'Today we slap the face of {1_CivName}; next year we dance on their grave.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOF_SO_AI_DOF_1', 'Being on such good terms, I couldn''t help but notice that you''d become friends with {1_CivName}. I have done the same. Perhaps this makeshift alliance will lead to great things in the future?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DENOUNCE_SO_AI_DENOUNCE_1', 'I share your convictions that {1_CivName} only causes problems for the civilized empires of the world. Perhaps the combination of our denouncements will teach them a lesson.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_1', 'Oh great, now the vermin are breeding. Now I have to deal with both you and {1_CivName}? I''ll have to teach them a lesson.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DENOUNCE_SO_AI_DOF_1', 'Anyone who you don''t get along with is clearly worth building a friendship with. Give {1_CivName} my regards.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_1', 'I see your armies amassed near my empire. If you wish to attack then come. Don''t hide in the shadows like a child.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_AGGRESSIVE_MILITARY_WARNING_1', 'It''s come to my attention that you have a large number of Units near my borders. I request that you move them elsewhere to avoid causing undue distress to my citizens.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_KILLED_PROTECTED_CITY_STATE_1', 'It seems your word is worth nothing, as your conquest of {@1_MinorCivName} shows. How despicable. The world will know of your crimes.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_ATTACKED_PROTECTED_CITY_STATE_1', 'Your wanton aggression against {@1_MinorCivName} does not go unnoticed. You will face serious consequences should you continue assault.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_EXPANSION_SERIOUS_WARNING_1', 'Your expansion efforts are an insult. Know that there will be consequences for this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_EXPANSION_WARNING_1', 'The proximity of our nations is causing tension between our peoples. Settle no new cities near me so that we may continue to work in harmony.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_EXPANSION_WARNING_2', 'My willingness to work with you dwindles as the borders of our nations draw nearer. I demand you not settle near my borders, lest you wish me to take action.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_EXPANSION_WARNING_3', 'As the borders of our nations draw nearer each year, my warmth toward you cools. Settling new cities near my sovereign territory could strain our relations beyond repair.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_EXPANSION_WARNING_4', 'We have discovered an ancient de jure claim on the lands between here and your capital. Should you expand further into our rightful dominion, it will only increase our glory when we usurp them!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_EXPANSION_BROKEN_PROMISE_1', 'Your guarantee of not expanding near us seems to have been empty words. Know that there will be consequences for this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PLOT_BUYING_SERIOUS_WARNING_1', 'The aggressive manner in which you buy up territory near us is an insult. I had hoped you would be a better neighbor.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PLOT_BUYING_WARNING_1', 'Your purchase of land in my vicinity has caused some alarm. I caution you so that this issue does not become more serious.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PLOT_BUYING_BROKEN_PROMISE_1', 'So it seems you lied when you told us that your empire would stop purchasing land near us. I do not take this insult lightly.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PLOT_BUYING_BROKEN_PROMISE_2', 'I am very disappointed that you did not stop funding land acquisition near my territory. I had hoped you were a more honorable leader.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_1', 'I thought you might like to know that I''ve launched an invasion of one of your little pet states. Their land and resources will make a fine addition to my own.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_2', 'Oops, it seems I''ve attacked one of your little friends. Care to do something about it?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_1', 'It''s come to my attention that I may have attacked a city-state with which you have had past relations. While it was not my goal to be at odds with your empire, this was deemed a necessary course of action.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_2', 'I''ve been informed that my empire has commenced an attack on a city-state friendly to you. I assure you, this was not on purpose, and hope that this does not serve to drive us apart.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_1', 'It takes a long time to grow an old friend. I''d like to form an Alliance, informing the other empires of the bond between our peoples. What do you say?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_2', 'It is important for our friends to believe we are unreservedly frank with them, and important to friendship that we are not. Would you care to ally with us?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_3', 'Teamwork gives us someone else to blame. Shall we announce to the world we are allies?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCE_1', 'I wish you no harm, but it would have been much better if you had never lived.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCE_2', 'The end may justify the means as long as there is something that justifies the end. [NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCE_3', 'I''d rather betray the world than let the world betray me.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_COOP_WAR_REQUEST_1', 'My disagreements with {1_PlayerName:textkey} have become too much to bear. This is a situation that can only be resolved on the battlefield. Will you join me, friend?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_COOP_WAR_REQUEST_2', '{1_PlayerName:textkey} has become a nuisance. One that can only be dealt with through war. I seek allies - can I count you among them?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_COOP_WAR_REQUEST_3', '{1_PlayerName:textkey} is a threat to the stability of this world. The time has come to deal with them once and for all. Will you assist me in this effort?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_COOP_WAR_TIME_1', 'The time for war against {1_PlayerName:textkey} has come.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_NOW_UNFORGIVABLE_1', 'Your actions are unforgivable. There can never be reconciliation between our two nations.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_NOW_ENEMY_1', 'Given the circumstances, I have no choice but to assume you are my enemy. As such, I will do what I must to protect my interests.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_COMPLIMENT_1', 'I wanted to make it known to you that I value our friendship, and hope that it might remain strong.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_BOOT_KISSING_1', 'It is clear to all that you are a wise and mighty ruler. I ask that you might also be compassionate towards those like myself who are weak.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_BOOT_KISSING_2', 'Oh great one, my hope is that you will long serve as a paragon of strength and virtue for lesser leaders such as myself.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WARMONGER_1', 'Your highly aggressive behavior has not gone unnoticed. Should you keep it up, it will ultimately lead to your downfall.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WARMONGER_2', 'It''s clear you''re a fairly uncivilized brute. I have to warn you, your actions are starting to attract quite a bit of attention.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WARMONGER_3', 'Your warmongering is a popular topic these days. Better watch your back.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WARMONGER_4', 'You''re starting to attract quite a bit of attention. That''s not a good thing.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_MINOR_CIV_COMPETITION_1', 'It''s come to my attention that you''re getting a little friendly with {1_MinorCivName:textkey}. In case you weren''t aware, they''re within my sphere of influence, and I''d prefer if you stayed out of it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_MINOR_CIV_COMPETITION_2', 'I''ve noticed your relationship with {1_MinorCivName:textkey}. While that''s nice, it would be best that you leave their protection to us. I''d hate for either of us to step on any toes.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_GENERIC_1', 'I couldn''t help but notice how pathetic you are. And when I realized it I couldn''t help but share.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_GENERIC_2', 'Ah, it''s always nice to see my favorite city-state again.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_GENERIC_3', 'I''m really getting tired of seeing your face. My hope is that one day you''ll just go away or something.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_GENERIC_4', 'Ah, it''s you. My apologies. Sometimes I get you mixed up with the barbarians I have to swat from time to time.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_MILITARY_1', 'I thought you might like to know that your army is a little on the weak side. Better watch your back.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_MILITARY_2', 'With an army so weak it''s a surprise your empire hasn''t succumb to a barbarian invasion.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_NUKE_1', 'Our words are backed with nuclear weapons!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_BULLY_1', 'It''s a shame there are some leaders in this world who have their fun by picking on the weak. Don''t you agree?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_UNHAPPINESS_1', 'I can hear your people wailing in sorrow all the way over in my empire. If you don''t do something soon they might just pack up and find a real civilization to live in.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_UNHAPPINESS_2', 'With people so unhappy it''s a shock to me that they''d keep you around as their leader.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_CITIES_1', 'It''s come to my attention that your empire appears to be quite puny. Did you forget to expand your borders or are you just having a little trouble with that?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_POPULATION_1', 'Travelers have told us that your empire''s economy is in pretty sad shape. If you beg a little I might give you a hand. Okay, actually I won''t.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_CULTURE_1', 'What a sad sight... your empire is so uncultured. What do your citizens do all day, beat rocks together?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_CULTURE_2', 'Rumor has it that your culture is so laughable that even the barbarians are shying away from your lands.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DECLARATION_PROTECT_CITY_STATE_1', '{2_MajorCivName:textkey} announces to the world that it is now protecting {1_MinorCivName:textkey}. Attacking the City-State will greatly damage relations and may lead to war.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DECLARATION_ABANDON_CITY_STATE_1', '{2_MajorCivName:textkey} has taken back its guarantee of protecting {1_MinorCivName:textkey}.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REPEAT_NO_1', 'As I said before, I never repeat myself. The answer is still no.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DONT_SETTLE_YES_1', 'Very well. I agree to avoid settling in the vicinity of your empire for the time being. Perhaps in the future you will be a more accommodating neighbor.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DONT_SETTLE_YES_2', 'So be it. I will not settle near your lands. Cooperation is clearly not one of your strong suits.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_1', 'Unacceptable. I am a sovereign and have the right to settle what lands I please. This offense will not be forgotten.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_2', 'You dare insult me in such a manner? I will not forget this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_3', 'How dare you? Do you believe me to be weak-willed?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_4', 'Not a chance. You need to brush up on your diplomacy, my friend.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WORK_WITH_US_YES_1', 'That will work. May a strong friendship lead to the flourishing of our empires.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WORK_WITH_US_YES_2', 'An excellent idea. May our alliance be the envy of the world.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WORK_WITH_US_YES_3', 'I am happy to accept. May our empires prosper in friendship.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DECLINE_1', 'I must decline.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DECLINE_2', 'Sorry, but no. Perhaps another time.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DECLINE_3', 'I fear that such an arrangement is not in my best interests, so I must decline.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WORK_AGAINST_SOMEONE_YES_1', 'It is agreed. Let us do what we can.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_COOP_WAR_YES_1', 'That sounds like a wise move. Let us take to the battlefield.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_COOP_WAR_SOON_1', 'That is an intriguing offer. I accept. However, my empire is not yet prepared, so give me 10 turns. I will come to you again when I am ready.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_YES_1', 'Very well. I do what I must to maintain peace in my lands.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_YES_2', 'I will do what it takes to keep the peace.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_YES_3', 'Fine. But know that you poison your reputation with acts such as these. For what that might be worth to you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_WEAK_1', 'Please. You''re in no position to make such demands.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_WEAK_2', 'You should be well aware of your own inability to back those threats up.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_WEAK_3', 'You''re not fooling anyone. I have nothing to fear from you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_WEAK_4', 'Haha! You are not strong enough to make such demands of me.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_HOSTILE_1', 'You dare insult me in such a manner? If you want something so badly then do your best to come and take it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_HOSTILE_2', 'You''ll have to take it from my dead body.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_HOSTILE_3', 'If you want that you''ll have to come and get it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_HOSTILE_4', 'Unacceptable.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_HOSTILE_5', 'Don''t be a fool.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_TOO_MUCH_1', 'You ask for too much. I will not drive my nation into poverty to satisfy your greed.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_TOO_MUCH_2', 'No. I have my dignity, and refuse to hand over such a great bounty so freely.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_TOO_MUCH_3', 'Your desire to strip my lands of so much wealth is intolerable. Do not ask for so much.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_TOO_SOON_1', 'You threaten me again so soon? I refuse your demands, you vile beast.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_TOO_SOON_2', 'Asking again so soon? You know that greed is a vice, great leader...', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_DEMAND_REFUSE_TOO_SOON_3', 'Asking for the fruits of others'' labor yet again? Your greed is legendary. I will not give in to you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOT_DOT_DOT', '...', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_1', 'Let''s hear your offer.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_2', 'What do you propose?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_OFFER_1', 'The time has come to end this war. What do you think of this arrangement?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_OFFER_2', 'There is no point in this conflict continuing any further. Let us return to peace.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_OFFER_3', 'We must not let war define who we are. The time has come to make peace.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DEMAND_1', 'I have the simplest tastes. I am always satisfied with the best. If you don''t give it, you shall meet my forces on the battlefield.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REQUEST_ITEM_1', 'A true friend never gets in the way, unless you happen to be going down. I ask for your assistance.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REQUEST_ITEM_2', 'Honesty is the best policy, when there is money in it. I ask for your aid.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REQUEST_ITEM_3', 'Can you help a friend in need? Do the right thing. It will gratify some people and astonish the rest.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GIFT_ITEM_1', 'Please accept this gift, great leader. May our friendship last forever.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GIFT_ITEM_2', 'Friends must support one another to overcome the challenges this world poses. Please accept this gift, great leader.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LUXURY_TRADE_1', 'You have a Luxury Resource that I would like. What do you think of this offer?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_1', 'I believe it''s in our mutual interest to allow free passage between our two great empires.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_2', 'Opening our borders to one another can only serve to solidify our friendship.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_OPEN_BORDERS_OFFER_1', 'I would like to negotiate passage through your territory.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PLAN_RESEARCH_AGREEMENT_1', 'I would like to lay plans for establishing a Research Agreement with your great nation. Doing so would require each of us to spend [ICON_GOLD] Gold. What do you think?[NEWLINE][NEWLINE](Agreeing will not form a Research Agreement yet)', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RESEARCH_AGREEMENT_OFFER_1', 'I feel that a Research Agreement is a mutually beneficial arrangement that would help serve to cement our alliance.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_1', 'Your offer is generous, and I accept.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_2', 'I graciously accept.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_3', 'I will never match such generosity. You have bought yourself our good will.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_1', 'That deal will work.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_2', 'Let''s set a date for the signing ceremony.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_AI_DEMAND_1', 'Pleasure doing business with you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_AI_DEMAND_2', 'It''s nice when neighbors can get along so well.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_HUMAN_CONCESSIONS_1', 'It is agreed. I believe this deal will help restore our friendship.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_HUMAN_CONCESSIONS_2', 'Your generosity is appreciated.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_1', 'How can you offer those terms with a straight face?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_2', 'You''ll have to do better than that.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_3', 'If you''re trying to be amusing, you''re failing miserably.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_1', 'If I agreed with you we''d both be wrong.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_2', 'I''ve had a perfectly wonderful evening, but this isn''t it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_3', 'I can please only one person per day. Today is not your day. Tomorrow isn''t looking good, either.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_DEAL_UNCHANGED_1', 'This deal will work as it stands on the table.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_DEAL_UNCHANGED_2', 'The deal looks good to me as it is.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_DEAL_UNCHANGED_3', 'Looks good to me.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_AI_MAKES_OFFER_1', 'I either get what I want, or I change my mind.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_AI_MAKES_OFFER_2', 'Money is the root of all of my wealth.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_AI_MAKES_OFFER_3', 'Here are my principles, and if you don''t like them... well, I have others.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_1', 'I''m sorry, I don''t see a way to make this deal work.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_2', 'There is no way to make this work.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_HOSTILE_1', 'You''ve been wrong about every single thing you''ve ever done, including this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_HOSTILE_2', 'You are pathetic. I would say that I was surprised by this but that would be a lie.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_HOSTILE_3', 'Death is hereditary. Yours will come soon.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_HOSTILE_4', 'You are clearly unfit to be the ruler of even the most pathetic kingdom.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_HOSTILE_5', 'Don''t think of death as an ending. Rather, think of it as an effective way of reducing your expenses.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_WEAK_HOSTILE_1', 'I may fail to overcome your evil, but it won''t be for lack of effort.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_WEAK_HOSTILE_2', 'Given the circumstances, destroying you would make victory all the sweeter.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_WEAK_HOSTILE_3', 'Destroy me if you can. I can promise you though, I will not fall before you easily.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_WEAK_HOSTILE_4', 'So be it. My armies will fight to the death against your wicked forces.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_WEAK_HOSTILE_5', 'You will forever be known as a villain who preys on the weak.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_STRONG_HOSTILE_1', 'Do you really think that you can defeat me? Fool. Very well. Let us play this game then.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_STRONG_HOSTILE_2', 'Your armies are welcome to come and enjoy the destruction that will befall them.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_STRONG_HOSTILE_3', 'Attacking me was a mistake. I will soon show you why.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_STRONG_HOSTILE_4', 'Declaring war was a serious error in judgment on your part. That having been said, I will not hesitate to make you regret it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_STRONG_HOSTILE_5', 'I will not hesitate to punish you for making such a foolish move.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_STRONG_HOSTILE_6', 'Good. Let us do this. It gives me an excellent opportunity to finally destroy you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_STRONG_HOSTILE_7', 'You wretched fool. I will now mercilessly crush you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_EXCITED_1', 'If you wish to fight, I certainly will not back down.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_EXCITED_2', 'Trading blows with you will be my pleasure. Make sure that you put up a fight.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_EXCITED_3', 'This should be fun. I revel in every opportunity to defeat someone like you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_EXCITED_4', 'Very well. A maelstrom of death shall envelope us both.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_WEAK_EXCITED_1', 'While now may not be the ideal moment for us to cross swords, the opportunity to take you down a notch or two is worth it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_STRONG_EXCITED_1', 'Haha. Well, I won''t question your decision, but it appears to me that you''ve made a big mistake.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_SAD_1', 'Your actions fill me with sadness... There is surely a special place in Hell for those such as you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_SAD_2', 'You would attack us in such a manner? Your hubris knows no limits. I hope that you will one day be judged for this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_SAD_3', 'Taking advantage of another''s weakness is unnecessary in the pursuit of victory. Surely you know there''s a better way?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_SAD_4', 'How could you do such a thing? Begone, you vile being.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_BETRAYED_1', 'Taking advantage of someone''s trust is despicable. Everyone will know of your betrayal.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_GENERIC_1', 'You are on the way to destruction.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_GENERIC_2', 'You have no chance to survive.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_GENERIC_3', 'You''ll be stone dead in a moment.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_GENERIC_4', 'Don''t think of death as an ending. Rather, think of it as an effective way of reducing your expenses.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_ATTACKED_MILITARY_PROMISE_BROKEN_1', 'You have betrayed your word. Make no mistake, the world will know of your treachery.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_LAND_1', 'All your cities belong to us.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_LAND_2', 'You are too close for comfort. My empire requires more living space.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_LAND_3', 'We are no longer going to ask for the land, but we are going to take it without negotiating.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_WORLD_CONQUEST_1', 'This world is destined to be mine. It is time for me to show you what that means.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_WORLD_CONQUEST_2', 'Fate has christened me the one which shall bring unity to this divided planet.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_WORLD_CONQUEST_3', 'History shall surely smile on me once I''ve destroyed the likes of you and the rest of the vermin which surround me.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_WORLD_CONQUEST_4', 'I''ll be honest with you - I intend to destroy you along with everyone else.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_WORLD_CONQUEST_5', 'You are merely a stepping-stone on my grand ascent to world domination.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_WORLD_CONQUEST_6', 'Now is the time for my master plan to commence. It is time for you to die like the rest of these fools.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_1', 'The advantage is mine. I will now destroy you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_2', 'I have waited long enough. I have seen an opening in your defense and I will use it to my advantage.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_3', 'I have been waiting a long time for this day. Now would be a good time to put your affairs in order.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_4', 'It is now my time. I will defeat you and come one step closer to victory.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_5', 'Fate is not on your side. Crossing my path with such weakness is a mistake you will not long regret.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_6', 'The current situation cannot be ignored. It is now or never.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_7', 'I cannot ignore this chance to eliminate a potential competitor.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_8', 'Your weakness presents to me a great opportunity. You have my apologies.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_DESPERATE_1', 'Although the task before me may be impossible, I am compelled to find a way to destroy you and all that you stand for. I will do so or die trying.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_DESPERATE_2', 'It is clear that you are strong. I will, however, find a way to defeat you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_DESPERATE_3', 'You are a villain without equal. Within every fiber of my being, I know I must destroy you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_DESPERATE_4', 'Your might is unmatched, but I will find a way to defeat you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_DESPERATE_5', 'The time to make a stand is now. The odds are not with me, but I find a way to destroy you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_DESPERATE_6', 'Your incredible strength will not deter me from finding a way to defeat you. May the fates smile on me.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_DESPERATE_7', 'Although I do not wish it, I fear that now may be my only opportunity to defeat you. Let us meet on the battlefield.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_DESPERATE_8', 'Destruction might be the fate which awaits me, but should I back down only eventual defeat awaits anyways.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_1', 'True friends stab you in the front.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_2', 'I''m afraid you''re about to become the immediate past president of the Being Alive Club.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_3', 'Backstab is such an ugly word. Shall we instead say I am just ''reinterpreting'' our supposed friendship?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_4', 'I have to admit, I never really liked you much. I''m a traitor, but I don''t consider myself a traitor. It''s time for you to die.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_WEAK_BETRAYAL_1', 'The odds are clearly not with me, but I see no alternative to revealing my deceptive practices. I can now only pray that my armies succeed.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_REGRET_1', 'I believe in another life the two of us could have been friends. Unfortunately, that is not how it is now. I wish your armies luck.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_REGRET_2', 'My friend, I fear that I have no choice but to break our ties and draw my sword. I hope that you might one day forgive me for this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_REGRET_3', 'I can hardly believe that it''s come to this, but yes, this is indeed happening. It is with great sorrow that I must inform you that our friendship must come to an end.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_WAR_DEMAND_REFUSED_1', 'So be it. My armies will extract it from your lands then.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_WAR_DEMAND_REFUSED_2', 'This is your fault. I''m going to kill you. And all the cake is gone. You don''t even care, do you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_1', 'What is your offer?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_2', 'What would you suggest?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_3', 'What is the price of peace?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_MADE_BY_HUMAN_GRACIOUS_1', 'I am happy to see an end to this conflict.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_MADE_BY_HUMAN_GRACIOUS_2', 'It is good to see the end of this terrible war.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_NO_PEACE_1', 'There can be no peace. Perhaps another day.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_NO_PEACE_2', 'This is not the time for negotiation.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_NO_PEACE_3', 'We still have business to settle.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TOO_SOON_NO_PEACE_1', 'You''re joking, right?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_PLEASED_1', 'Excellent.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_PLEASED_2', 'Wonderful.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_PLEASED_3', 'I am pleased to hear this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_PLEASED_4', 'That is good to hear.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_1', 'You have my thanks.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_2', 'You are truly a great leader.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_3', 'History will long remember your great deeds.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_DISAPPOINTED_1', 'That is a shame.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_DISAPPOINTED_2', 'Perhaps another time then.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_DISAPPOINTED_3', 'That is disappointing.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_SO_BE_IT_1', 'So be it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_SO_BE_IT_2', 'Then you will be responsible for the consequences.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_SO_BE_IT_3', 'You will regret this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_1', 'For freeing those captives, you have my thanks.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_2', 'I am appreciative for the actions you have taken on behalf of my people.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_3', 'Thank you. May this be remembered as a historic event which brought our two great empires closer together.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_CULTURE_BOMBED_1', 'How dare you? Know that there will be consequences for this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_CULTURE_BOMBED_2', 'If you are trying to provoke a war, you''re doing a fine job.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_1', 'Well, at least you''re honest. But that may be your only good trait.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_GOOD_1', 'A likely story.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_AGGRESSIVE_MILITARY_WARNING_BAD_1', 'That is what I was afraid of.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_AGGRESSIVE_MILITARY_WARNING_GOOD_1', 'I only hope that is indeed the case.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HOSTILE_WE_ATTACKED_MINOR_BAD_1', 'We''ll see about that.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HOSTILE_WE_ATTACKED_MINOR_GOOD_1', 'Yes, that''s right.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WE_ATTACKED_MINOR_BAD_1', 'I remain confident that my decision was the best for my people and my nation.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_1', 'It seems that you cannot be reasoned with. What a shame.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_2', 'I have no more business with the likes of you then.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_3', 'You disappoint us.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_4', 'You know that this can only sow tension between our two nations.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_ATTACKED_MINOR_GOOD_1', 'Very well. I''ll be monitoring the situation.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_KILLED_MINOR_GOOD_1', 'And what would you suggest?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_SERIOUS_EXPANSION_WARNING_GOOD_1', 'And what would you suggest?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_EXPANSION_WARNING_GOOD_1', 'Excellent. I hope that we can continue to find common ground.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_SERIOUS_PLOT_BUYING_WARNING_GOOD_1', 'And what would you suggest?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_PLOT_BUYING_WARNING_GOOD_1', 'Excellent. I hope that we can continue to find common ground.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_OUR_WORDS_ARE_BACKED', 'Our words are backed with nuclear weapons!', '', '');
